CREATE OR REPLACE FUNCTION role_effective_permissions_mx_on_delete()
RETURNS TRIGGER AS $$
DECLARE
  project_ids BIGINT[];
  role_ids    BIGINT[];
BEGIN
  SELECT ARRAY_AGG(DISTINCT "ROLE_ID")
    INTO role_ids
    FROM old_table;

  IF TG_TABLE_NAME = 'ROLES_PERMISSIONS' THEN
    SELECT ARRAY_AGG(sub."PROJECT_ID")
      INTO project_ids
      FROM (
        SELECT upr."PROJECT_ID"
          FROM "USERS_PROJECTS_ROLES" upr
         INNER JOIN old_table
            ON old_table."ROLE_ID" = upr."ROLE_ID"
      ) sub;
  ELSE
    SELECT ARRAY_AGG(DISTINCT "PROJECT_ID")
      INTO project_ids
      FROM "USERS_PROJECTS_ROLES"
     WHERE "ROLE_ID" = ANY(role_ids);
  END IF;

  PERFORM recalc_user_project_role_effective_permissions(project_ids);
  RETURN NULL;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION role_effective_permissions_mx_on_insert()
RETURNS TRIGGER AS $$
DECLARE
  project_ids BIGINT[];
  role_ids    BIGINT[];
BEGIN
  SELECT ARRAY_AGG(DISTINCT "ROLE_ID")
    INTO role_ids
    FROM new_table;

  IF TG_TABLE_NAME = 'ROLES_PERMISSIONS' THEN
    SELECT ARRAY_AGG(sub."PROJECT_ID")
      INTO project_ids
      FROM (
        SELECT upr."PROJECT_ID"
          FROM "USERS_PROJECTS_ROLES" upr
         INNER JOIN new_table
            ON new_table."ROLE_ID" = upr."ROLE_ID"
      ) sub;
  ELSE
    SELECT ARRAY_AGG(DISTINCT "PROJECT_ID")
      INTO project_ids
      FROM "USERS_PROJECTS_ROLES"
     WHERE "ROLE_ID" = ANY(role_ids);
  END IF;

  PERFORM recalc_user_project_role_effective_permissions(project_ids);
  RETURN NULL;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION role_effective_permissions_mx_on_update()
RETURNS TRIGGER AS $$
DECLARE
  project_ids BIGINT[];
  role_ids    BIGINT[];
BEGIN
  SELECT ARRAY_AGG("ROLE_ID")
    INTO role_ids
    FROM (
      SELECT "ROLE_ID" FROM old_table
       UNION
      SELECT "ROLE_ID" FROM new_table
    ) roles;

  IF TG_TABLE_NAME = 'ROLES_PERMISSIONS' THEN
    SELECT ARRAY_AGG(sub."PROJECT_ID")
      INTO project_ids
      FROM (
        SELECT upr."PROJECT_ID"
          FROM "USERS_PROJECTS_ROLES" upr
         INNER JOIN new_table
            ON new_table."ROLE_ID" = upr."ROLE_ID"
          FULL OUTER JOIN old_table
            ON new_table."ROLE_ID" = old_table."ROLE_ID"
      ) sub;
  ELSE
    SELECT ARRAY_AGG(DISTINCT "PROJECT_ID")
      INTO project_ids
      FROM "USERS_PROJECTS_ROLES"
     WHERE "ROLE_ID" = ANY(role_ids);
  END IF;

  PERFORM recalc_user_project_role_effective_permissions(project_ids);
  RETURN NULL;
END;
$$ LANGUAGE plpgsql;

-- INSERT trigger for USERS_PROJECTS_ROLES CREATE TRIGGER trigger_effective_permissions_mx_on_users_roles_insert AFTER INSERT ON "USERS_PROJECTS_ROLES" REFERENCING NEW TABLE AS new_table FOR EACH STATEMENT EXECUTE FUNCTION role_effective_permissions_mx_on_insert();
-- DELETE trigger for USERS_PROJECTS_ROLES CREATE TRIGGER trigger_effective_permissions_mx_on_users_roles_delete AFTER DELETE ON "USERS_PROJECTS_ROLES" REFERENCING OLD TABLE AS old_table FOR EACH STATEMENT EXECUTE FUNCTION role_effective_permissions_mx_on_delete();
-- UPDATE trigger for USERS_PROJECTS_ROLES CREATE TRIGGER trigger_effective_permissions_mx_on_users_roles_update AFTER UPDATE ON "USERS_PROJECTS_ROLES" REFERENCING OLD TABLE AS old_table NEW TABLE AS new_table FOR EACH STATEMENT EXECUTE FUNCTION role_effective_permissions_mx_on_update();
-- INSERT trigger for ROLES_PERMISSIONS CREATE TRIGGER trigger_effective_permissions_mx_on_roles_permissions_insert AFTER INSERT ON "ROLES_PERMISSIONS" REFERENCING NEW TABLE AS new_table FOR EACH STATEMENT EXECUTE FUNCTION role_effective_permissions_mx_on_insert();
-- DELETE trigger for ROLES_PERMISSIONS CREATE TRIGGER trigger_effective_permissions_mx_on_roles_permissions_delete AFTER DELETE ON "ROLES_PERMISSIONS" REFERENCING OLD TABLE AS old_table FOR EACH STATEMENT EXECUTE FUNCTION role_effective_permissions_mx_on_delete();
-- UPDATE trigger for ROLES_PERMISSIONS CREATE TRIGGER trigger_effective_permissions_mx_on_roles_permissions_update AFTER UPDATE ON "ROLES_PERMISSIONS" REFERENCING OLD TABLE AS old_table NEW TABLE AS new_table FOR EACH STATEMENT EXECUTE FUNCTION effective_permissions_mx_on_update();
