CREATE FUNCTION project_hierarchy_maintenance_on_project_insert()
RETURNS TRIGGER AS $$
BEGIN
  INSERT INTO "PROJECT_HIERARCHY" ("PARENT_PROJECT_ID", "CHILD_PROJECT_ID", "DEPTH")
  VALUES(NEW."ID", NEW."ID", 0);

  INSERT INTO "PROJECT_HIERARCHY" ("PARENT_PROJECT_ID", "CHILD_PROJECT_ID", "DEPTH")
  SELECT "PARENT_PROJECT_ID", NEW."ID", "DEPTH" + 1
    FROM "PROJECT_HIERARCHY"
   WHERE "CHILD_PROJECT_ID" = NEW."PARENT_PROJECT_ID";

  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE FUNCTION project_hierarchy_maintenance_on_project_update()
RETURNS TRIGGER AS $$
BEGIN
  DELETE FROM "PROJECT_HIERARCHY" WHERE "CHILD_PROJECT_ID" = old."ID";

  INSERT INTO "PROJECT_HIERARCHY" ("PARENT_PROJECT_ID", "CHILD_PROJECT_ID", "DEPTH")
  VALUES (NEW."ID", NEW."ID", 0);

  INSERT INTO "PROJECT_HIERARCHY" ("PARENT_PROJECT_ID", "CHILD_PROJECT_ID", "DEPTH")
  SELECT "PARENT_PROJECT_ID", NEW."ID", "DEPTH" + 1
    FROM "PROJECT_HIERARCHY"
   WHERE "CHILD_PROJECT_ID" = NEW."PARENT_PROJECT_ID";

  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE FUNCTION project_hierarchy_maintenance_on_project_delete()
RETURNS TRIGGER AS $$
BEGIN
  DELETE FROM "PROJECT_HIERARCHY"
   WHERE "PARENT_PROJECT_ID" IN (SELECT "ID" FROM old_table)
      OR "CHILD_PROJECT_ID" IN (SELECT "ID" FROM old_table);

  RETURN NULL;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER trigger_project_hierarchy_maintenance_on_project_insert AFTER INSERT ON "PROJECT" FOR EACH ROW EXECUTE FUNCTION project_hierarchy_maintenance_on_project_insert();
CREATE TRIGGER trigger_project_hierarchy_maintenance_on_project_update AFTER UPDATE OF "PARENT_PROJECT_ID" ON "PROJECT" FOR EACH ROW WHEN (OLD."PARENT_PROJECT_ID" IS DISTINCT FROM NEW."PARENT_PROJECT_ID") EXECUTE FUNCTION project_hierarchy_maintenance_on_project_update();
CREATE TRIGGER trigger_project_hierarchy_maintenance_on_project_delete AFTER DELETE ON "PROJECT" REFERENCING OLD TABLE AS old_table FOR EACH STATEMENT EXECUTE FUNCTION project_hierarchy_maintenance_on_project_delete();

-- Constraint to ensure one and only one of "LDAPUSER_ID", "MANAGEDUSER_ID", and "OIDCUSER_ID" is set ALTER TABLE "USER_PROJECT_EFFECTIVE_PERMISSIONS" ADD CHECK ( ("LDAPUSER_ID" IS NOT NULL)::INTEGER + ("MANAGEDUSER_ID" IS NOT NULL)::INTEGER + ("OIDCUSER_ID" IS NOT NULL)::INTEGER = 1);
-- Composite unique index on "LDAPUSER_ID", "PROJECT_ID", and "PERMISSION_ID" columns CREATE UNIQUE INDEX "USER_PROJECT_EFFECTIVE_PERMISSIONS_LDAPUSERS_IDX" ON "USER_PROJECT_EFFECTIVE_PERMISSIONS" USING btree ("LDAPUSER_ID", "PROJECT_ID", "PERMISSION_ID") WHERE "LDAPUSER_ID" IS NOT NULL;
-- Composite unique index on "MANAGEDUSER_ID", "PROJECT_ID", and "PERMISSION_ID" columns CREATE UNIQUE INDEX "USER_PROJECT_EFFECTIVE_PERMISSIONS_MANAGEDUSERS_IDX" ON "USER_PROJECT_EFFECTIVE_PERMISSIONS" USING btree ("MANAGEDUSER_ID", "PROJECT_ID", "PERMISSION_ID") WHERE "MANAGEDUSER_ID" IS NOT NULL;
-- Composite unique index on "OIDCUSER_ID", "PROJECT_ID", and "PERMISSION_ID" columns CREATE UNIQUE INDEX "USER_PROJECT_EFFECTIVE_PERMISSIONS_OIDCUSERS_IDX" ON "USER_PROJECT_EFFECTIVE_PERMISSIONS" USING btree ("OIDCUSER_ID", "PROJECT_ID", "PERMISSION_ID") WHERE "OIDCUSER_ID" IS NOT NULL;

-- Helper function to recalculate all user permissions for a project. -- Called by trigger functions to update the values in the USER_PROJECT_EFFECTIVE_PERMISSIONS table. CREATE OR REPLACE FUNCTION recalc_user_project_effective_permissions(project_ids BIGINT[]) RETURNS void AS $$ BEGIN -- Remove any existing effective permissions for this project. DELETE FROM "USER_PROJECT_EFFECTIVE_PERMISSIONS" WHERE "PROJECT_ID" = ANY(project_ids);
-- Rebuild effective permissions for LDAP users INSERT INTO "USER_PROJECT_EFFECTIVE_PERMISSIONS" ("LDAPUSER_ID", "PROJECT_ID", "PERMISSION_ID", "PERMISSION_NAME") SELECT DISTINCT lt."LDAPUSER_ID", pat."PROJECT_ID", tp."PERMISSION_ID", p."NAME" FROM "PROJECT_ACCESS_TEAMS" pat INNER JOIN "TEAMS_PERMISSIONS" tp ON tp."TEAM_ID" = pat."TEAM_ID" INNER JOIN "PERMISSION" p ON p."ID" = tp."PERMISSION_ID" INNER JOIN "LDAPUSERS_TEAMS" lt ON lt."TEAM_ID" = pat."TEAM_ID" WHERE pat."PROJECT_ID" = ANY(project_ids);
-- Rebuild effective permissions for managed users INSERT INTO "USER_PROJECT_EFFECTIVE_PERMISSIONS" ("MANAGEDUSER_ID", "PROJECT_ID", "PERMISSION_ID", "PERMISSION_NAME") SELECT DISTINCT mt."MANAGEDUSER_ID", pat."PROJECT_ID", tp."PERMISSION_ID", p."NAME" FROM "PROJECT_ACCESS_TEAMS" pat INNER JOIN "TEAMS_PERMISSIONS" tp ON tp."TEAM_ID" = pat."TEAM_ID" INNER JOIN "PERMISSION" p ON p."ID" = tp."PERMISSION_ID" INNER JOIN "MANAGEDUSERS_TEAMS" mt ON mt."TEAM_ID" = pat."TEAM_ID" WHERE pat."PROJECT_ID" = ANY(project_ids);
-- Rebuild effective permissions for OIDC users INSERT INTO "USER_PROJECT_EFFECTIVE_PERMISSIONS" ("OIDCUSER_ID", "PROJECT_ID", "PERMISSION_ID", "PERMISSION_NAME") SELECT DISTINCT ot."OIDCUSERS_ID", pat."PROJECT_ID", tp."PERMISSION_ID", p."NAME" FROM "PROJECT_ACCESS_TEAMS" pat INNER JOIN "TEAMS_PERMISSIONS" tp ON tp."TEAM_ID" = pat."TEAM_ID" INNER JOIN "PERMISSION" p ON p."ID" = tp."PERMISSION_ID" INNER JOIN "OIDCUSERS_TEAMS" ot ON ot."TEAM_ID" = pat."TEAM_ID" WHERE pat."PROJECT_ID" = ANY(project_ids); END; $$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION effective_permissions_mx_on_delete()
RETURNS TRIGGER AS $$
DECLARE
  project_ids BIGINT[];
BEGIN
  IF TG_TABLE_NAME = 'PROJECT_ACCESS_TEAMS' THEN
    PERFORM recalc_user_project_effective_permissions(
      (SELECT ARRAY_AGG(DISTINCT "PROJECT_ID") FROM old_table)
    );
  ELSIF TG_TABLE_NAME IN (
    'LDAPUSERS_TEAMS', 'MANAGEDUSERS_TEAMS', 'OIDCUSERS_TEAMS', 'TEAMS_PERMISSIONS'
  ) THEN
    PERFORM recalc_user_project_effective_permissions((
      SELECT ARRAY_AGG(DISTINCT pat."PROJECT_ID")
        FROM "PROJECT_ACCESS_TEAMS" AS pat
       INNER JOIN old_table
          ON old_table."TEAM_ID" = pat."TEAM_ID"
    ));
  END IF;
  RETURN NULL;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION effective_permissions_mx_on_insert()
RETURNS TRIGGER AS $$
DECLARE
  project_ids BIGINT[];
BEGIN
  IF TG_TABLE_NAME = 'PROJECT_ACCESS_TEAMS' THEN
    PERFORM recalc_user_project_effective_permissions(
      (SELECT ARRAY_AGG(DISTINCT "PROJECT_ID") FROM new_table)
    );
  ELSIF TG_TABLE_NAME IN (
    'LDAPUSERS_TEAMS', 'MANAGEDUSERS_TEAMS', 'OIDCUSERS_TEAMS', 'TEAMS_PERMISSIONS'
  ) THEN
    PERFORM recalc_user_project_effective_permissions((
      SELECT ARRAY_AGG(DISTINCT pat."PROJECT_ID")
        FROM "PROJECT_ACCESS_TEAMS" AS pat
       INNER JOIN new_table
          ON new_table."TEAM_ID" = pat."TEAM_ID"
    ));
  END IF;
  RETURN NULL;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION effective_permissions_mx_on_update()
RETURNS TRIGGER AS $$
DECLARE
  project_ids BIGINT[];
BEGIN
  IF TG_TABLE_NAME = 'PROJECT_ACCESS_TEAMS' THEN
    PERFORM recalc_user_project_effective_permissions((
      SELECT ARRAY_AGG("PROJECT_ID")
        FROM (
          SELECT "PROJECT_ID" FROM old_table
          UNION
          SELECT "PROJECT_ID" FROM new_table
        ) AS combined_projects
    ));
  ELSIF TG_TABLE_NAME IN (
    'LDAPUSERS_TEAMS', 'MANAGEDUSERS_TEAMS', 'OIDCUSERS_TEAMS', 'TEAMS_PERMISSIONS'
  ) THEN
    PERFORM recalc_user_project_effective_permissions((
      SELECT ARRAY_AGG(DISTINCT pat."PROJECT_ID")
        FROM "PROJECT_ACCESS_TEAMS" pat
        JOIN (
          SELECT "TEAM_ID" FROM old_table
          UNION
          SELECT "TEAM_ID" FROM new_table
        ) AS teams
          ON pat."TEAM_ID" = teams."TEAM_ID"
    ));
  END IF;
  RETURN NULL;
END;
$$ LANGUAGE plpgsql;

CREATE OR REPLACE FUNCTION prevent_direct_effective_permissions_writes()
RETURNS TRIGGER AS $$
BEGIN
  -- Depth of 1 means this trigger was fired by an attempted direct
  -- insert, update, or delete on USER_PROJECT_EFFECTIVE_PERMISSIONS.
  -- Depth should be 2, meaning this trigger was fired from another trigger.
  IF pg_trigger_depth() < 2 THEN
    RAISE EXCEPTION 'Direct modifications to USER_PROJECT_EFFECTIVE_PERMISSIONS are not allowed.';
  END IF;
  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

