changeSet.sql:
DO $$ DECLARE -- List of tables to update referencing_tables TEXT[] := ARRAY['TEAMS_PERMISSIONS', 'USERS_PERMISSIONS', 'ROLES_PERMISSIONS']; tbl_name TEXT; id_col TEXT; BEGIN -- Loop through our arrays by index FOREACH tbl_name IN ARRAY referencing_tables LOOP id_col := regexp_replace(tbl_name, '([A-Z]+?)S_PERMISSIONS', '\1') || '_ID';
-- Build dynamic INSERT statement EXECUTE format($fmt$ -- Input mapping of old permission names to their updated counterparts WITH cte_permission_map (old_names, new_names) AS ( VALUES ('ACCESS_MANAGEMENT%%', ARRAY['ACCESS_MANAGEMENT']), ('BOM_UPLOAD', ARRAY['BOM_CREATE', 'BOM_READ']), ('POLICY_MANAGEMENT%%', ARRAY['POLICY_MANAGEMENT', 'POLICY_VIOLATION_CREATE']), ('POLICY_VIOLATION_ANALYSIS', ARRAY['POLICY_VIOLATION_UPDATE']), ('PORTFOLIO_MANAGEMENT%%', ARRAY['PORTFOLIO_MANAGEMENT', 'PROJECT_UPDATE', 'PROJECT_DELETE']), ('PROJECT_CREATION_UPLOAD', ARRAY['PORTFOLIO_MANAGEMENT', 'BOM_CREATE', 'BOM_READ']), ('SYSTEM_CONFIGURATION%%', ARRAY['SYSTEM_CONFIGURATION', 'NOTIFICATION_RULE_MANAGEMENT']), ('TAG_MANAGEMENT%%', ARRAY['TAG_MANAGEMENT']), ('VIEW_BADGES', ARRAY['BADGES_READ']), ('VIEW_POLICY_VIOLATION', ARRAY['POLICY_VIOLATION_READ']), ('VIEW_PORTFOLIO', ARRAY['PROJECT_READ']), ('VIEW_VULNERABILITY', ARRAY['FINDING_READ']), ('VULNERABILITY_ANALYSIS', ARRAY['VULNERABILITY_MANAGEMENT']), ('VULNERABILITY_ANALYSIS_CREATE', ARRAY['FINDING_CREATE']), ('VULNERABILITY_ANALYSIS_READ', ARRAY['FINDING_READ']), ('VULNERABILITY_ANALYSIS_UPDATE', ARRAY['FINDING_UPDATE']), ('VULNERABILITY_MANAGEMENT%%', ARRAY['VULNERABILITY_MANAGEMENT']) ), -- Get IDs of entities mapped to old permissions cte_entities AS ( SELECT t.%I AS id FROM %I AS t INNER JOIN "PERMISSION" AS p ON p."ID" = t."PERMISSION_ID" INNER JOIN cte_permission_map ON p."NAME" LIKE cte_permission_map.old_names ), -- Get IDs of new permissions cte_new_permissions AS ( SELECT p_new."ID" AS id FROM cte_permission_map INNER JOIN "PERMISSION_NEW" AS p_new ON p_new."NAME" = ANY(cte_permission_map.new_names) ) INSERT INTO %I (%I, "PERMISSION_ID") SELECT cte_entities.id, cte_new_permissions.id FROM cte_entities, cte_new_permissions ON CONFLICT DO NOTHING; $fmt$, id_col, tbl_name, tbl_name || '_NEW', id_col );
RAISE NOTICE 'Updated table "%" on column "%" â†’ new permission', tbl_name, id_col; END LOOP; END $$ LANGUAGE plpgsql;

ALTER TABLE "PERMISSION" RENAME CONSTRAINT "PERMISSION_NEW_PK" TO "PERMISSION_PK";

ALTER TABLE "USERS_PERMISSIONS" RENAME CONSTRAINT "USERS_PERMISSIONS_NEW_PK" TO "USERS_PERMISSIONS_PK";
ALTER TABLE "USERS_PERMISSIONS" RENAME CONSTRAINT "USERS_PERMISSIONS_NEW_USER_FK" TO "USERS_PERMISSIONS_USER_FK";
ALTER TABLE "USERS_PERMISSIONS" RENAME CONSTRAINT "USERS_PERMISSIONS_NEW_PERMISSION_FK" TO "USERS_PERMISSIONS_PERMISSION_FK";

ALTER TABLE "TEAMS_PERMISSIONS" RENAME CONSTRAINT "TEAMS_PERMISSIONS_NEW_PK" TO "TEAMS_PERMISSIONS_PK";
ALTER TABLE "TEAMS_PERMISSIONS" RENAME CONSTRAINT "TEAMS_PERMISSIONS_NEW_TEAM_FK" TO "TEAMS_PERMISSIONS_TEAM_FK";
ALTER TABLE "TEAMS_PERMISSIONS" RENAME CONSTRAINT "TEAMS_PERMISSIONS_NEW_PERMISSION_FK" TO "TEAMS_PERMISSIONS_PERMISSION_FK";

ALTER TABLE "ROLES_PERMISSIONS" RENAME CONSTRAINT "ROLES_PERMISSIONS_NEW_PK" TO "ROLES_PERMISSIONS_PK";
ALTER TABLE "ROLES_PERMISSIONS" RENAME CONSTRAINT "ROLES_PERMISSIONS_NEW_ROLE_FK" TO "ROLES_PERMISSIONS_ROLE_FK";
ALTER TABLE "ROLES_PERMISSIONS" RENAME CONSTRAINT "ROLES_PERMISSIONS_NEW_PERMISSION_FK" TO "ROLES_PERMISSIONS_PERMISSION_FK";

DROP TRIGGER IF EXISTS trigger_effective_permissions_mx_on_teams_permissions_insert ON "TEAMS_PERMISSIONS"; DROP TRIGGER IF EXISTS trigger_effective_permissions_mx_on_teams_permissions_delete ON "TEAMS_PERMISSIONS"; DROP TRIGGER IF EXISTS trigger_effective_permissions_mx_on_teams_permissions_update ON "TEAMS_PERMISSIONS";
-- INSERT trigger for TEAMS_PERMISSIONS CREATE TRIGGER trigger_effective_permissions_mx_on_teams_permissions_insert AFTER INSERT ON "TEAMS_PERMISSIONS" REFERENCING NEW TABLE AS new_table FOR EACH STATEMENT EXECUTE FUNCTION effective_permissions_mx_on_insert();
-- DELETE trigger for TEAMS_PERMISSIONS CREATE TRIGGER trigger_effective_permissions_mx_on_teams_permissions_delete AFTER DELETE ON "TEAMS_PERMISSIONS" REFERENCING OLD TABLE AS old_table FOR EACH STATEMENT EXECUTE FUNCTION effective_permissions_mx_on_delete();
-- UPDATE trigger for TEAMS_PERMISSIONS CREATE TRIGGER trigger_effective_permissions_mx_on_teams_permissions_update AFTER UPDATE ON "TEAMS_PERMISSIONS" REFERENCING OLD TABLE AS old_table NEW TABLE AS new_table FOR EACH STATEMENT EXECUTE FUNCTION effective_permissions_mx_on_update();
DROP TRIGGER IF EXISTS trigger_effective_permissions_mx_on_roles_permissions_insert ON "ROLES_PERMISSIONS"; DROP TRIGGER IF EXISTS trigger_effective_permissions_mx_on_roles_permissions_delete ON "ROLES_PERMISSIONS"; DROP TRIGGER IF EXISTS trigger_effective_permissions_mx_on_roles_permissions_update ON "ROLES_PERMISSIONS";
-- INSERT trigger for ROLES_PERMISSIONS CREATE TRIGGER trigger_effective_permissions_mx_on_roles_permissions_insert AFTER INSERT ON "ROLES_PERMISSIONS" REFERENCING NEW TABLE AS new_table FOR EACH STATEMENT EXECUTE FUNCTION role_effective_permissions_mx_on_insert();
-- DELETE trigger for ROLES_PERMISSIONS CREATE TRIGGER trigger_effective_permissions_mx_on_roles_permissions_delete AFTER DELETE ON "ROLES_PERMISSIONS" REFERENCING OLD TABLE AS old_table FOR EACH STATEMENT EXECUTE FUNCTION role_effective_permissions_mx_on_delete();
-- UPDATE trigger for ROLES_PERMISSIONS CREATE TRIGGER trigger_effective_permissions_mx_on_roles_permissions_update AFTER UPDATE ON "ROLES_PERMISSIONS" REFERENCING OLD TABLE AS old_table NEW TABLE AS new_table FOR EACH STATEMENT EXECUTE FUNCTION role_effective_permissions_mx_on_update();

DROP TRIGGER IF EXISTS trigger_prevent_direct_effective_permissions_writes ON "USER_PROJECT_EFFECTIVE_PERMISSIONS";

-- Backfill the USER_PROJECT_EFFECTIVE_PERMISSIONS table for existing PROJECT_ACCESS_TEAMS entries
DO $$
BEGIN
  PERFORM recalc_user_project_effective_permissions((SELECT ARRAY_AGG("ID") FROM "PROJECT"));
END $$
LANGUAGE plpgsql;

-- Prevent direct inserts/updates/writes to USER_PROJECT_EFFECTIVE_PERMISSIONS
CREATE TRIGGER trigger_prevent_direct_effective_permissions_writes
BEFORE DELETE OR INSERT OR UPDATE ON "USER_PROJECT_EFFECTIVE_PERMISSIONS"
FOR EACH STATEMENT
EXECUTE FUNCTION prevent_direct_effective_permissions_writes();

